CREATE TABLE "events" (
	"id" integer PRIMARY KEY GENERATED BY DEFAULT AS IDENTITY (sequence name "events_id_seq" INCREMENT BY 1 MINVALUE 1 MAXVALUE 2147483647 START WITH 1 CACHE 1),
	"name" varchar(255) NOT NULL,
	"description" text,
	"start_date" timestamp with time zone NOT NULL,
	"end_date" timestamp with time zone NOT NULL,
	"location" varchar(500) NOT NULL,
	"category" varchar(100),
	"sub_category" varchar(100),
	"status" varchar(50) DEFAULT 'draft' NOT NULL,
	"tickets_sold" integer DEFAULT 0,
	"total_revenue" integer DEFAULT 0,
	"unique_attendees" integer DEFAULT 0,
	"image_url" varchar(500),
	"logo_url" varchar(500),
	"policy" varchar(255),
	"organizer" varchar(255),
	"organizer_logo" varchar(500),
	"teams" jsonb,
	"tags" jsonb,
	"timezone" varchar(100) DEFAULT 'GMT-6',
	"created_at" timestamp with time zone DEFAULT now(),
	"updated_at" timestamp with time zone DEFAULT now()
);
